mapper.select:
SELECT o.branch_id, b.branch_name, o.order_status, o.order_date,  oi.parts_id, p.part_name, oi.order_item_quantity, oi.order_item_price, o.order_id, o.order_deny
FROM order_item oi
JOIN parts p ON oi.parts_id = p.part_id
JOIN orders o ON oi.order_id = o.order_id
JOIN branch b ON o.branch_id = b.branch_id
WHERE 1 = 1

    AND o.branch_id = #{branchId}


    AND b.branch_name = #{branchName}


     AND o.order_status = #{orderStatus}


    AND o.order_date BETWEEN #{startDate} AND #{endDate}
